INSERT INTO `shixi` VALUES (226, '区块链后端开发工程师', '2023-05-05 19:54:33', '150-300/天', '南京', '不限', '5天／周', '实习3个月', '\n	Golang/NodeJS/Java后端开发实习\n\n\n	职位描述：\n\n\n	1、参与项目的系统功能设计和数据库设计；\n\n\n	2、软件系统设计、代码编写及相关文档编写等开发工作；\n\n\n	3、参与公司项目的开发，软件测试、系统性能优化等工作；\n\n\n	4、完成个人承担模块的开发和测试；\n\n\n	5、能够独自分析功能需求\n\n\n	 \n\n\n	任职要求：\n\n\n	1、有Golang、Nodejs或者Java应用软件开发经验；\n\n\n	2、熟练使用关系型数据库，有NoSql数据库（redis/mongodb等）设计和应用经验为佳；\n\n\n	3、有软件测试和优化经验；\n\n\n	4、具备良好的沟通能力和学习能力，责任心强。\n\n\n	 \n\n\n	优先考虑：\n\n\n	1、深入理解分布式、多线程等高性能架构相关开发技术；\n\n\n	2、熟悉前端框架，如React/Vue/Flutter/AngularJS；\n\n\n	3、对区块链、数字货币、交易所、智能合约有了解。\n\n\n	 \n', '商会大厦黄金楼', '投递要求： \n              简历要求：\n              中文 截止日期：2024-12-31', '2024-12-19 21:29:37', 'https://www.shixiseng.com/intern/inn_owp7z0q531d5?pcm=pc_SearchList');
