INSERT INTO `shixi` VALUES (373, '后端实习生', '2023-05-07 21:25:45', '200-400/天', '上海', '本科', '5天／周', '实习6个月', '\n	岗位职责:\n\n\n	1、负责公司内部系统的研发工作；\n\n\n	2、深入发掘和分析业务需求，撰写技术方案和系统设计；\n\n\n	3、对产品需求进行构建，部署；\n\n\n	4、持续对系统架构进行改造和性能优化；\n\n\n	任职要求:\n\n\n	1、本科及以上学历，计算机相关专业；\n\n\n	2、深刻理解计算机原理，良好的常用数据结构和算法基础；\n\n\n	3、有扎实的编程能力，有优秀的设计和代码品位；\n\n\n	4、熟练掌握至少一门编程语言 C/C++/Java 等不限；\n\n\n	5、熟悉 HTTP/HTTPS 协议；\n\n\n	6、熟悉 MySQL 数据库，了解索引优化、查询优化；\n\n\n	7、熟悉至少一种非关系型数据库，如 Redis，理解其适用场景；\n\n\n	8、熟悉消息队列的架构范式；\n\n\n	9、熟悉 Docker 和 Kubernetes 优先；\n\n\n	10、熟悉 PHP/Go 语言优先；\n\n\n	 \n', '上海市长宁区淞虹路207号明基商务广场B座', '投递要求： \n              简历要求：\n              中文 截止日期：2024-12-31', '2024-12-19 21:34:02', 'https://www.shixiseng.com/intern/inn_uiozrk685els?pcm=pc_SearchList');
